import styled from 'styled-components'

export const Heading = styled.h1`
    margin-top: 0;
    margin-bottom: 0;
    font-size: 36px;
    font-weight: bold;

    @media (min-width: 480px) {
        font-size: 48px;
    }

    @media (min-width: 768px) {
        font-size: 72px;
    }

    & + h1,
    & + h2,
    & + p {
      margin-top: 21px;
    }
`

export const Subheading = styled.h2`
    margin-top: 0;
    margin-bottom: 0;
    font-size: 24px;
    font-weight: bold;

    @media (min-width: 480px) {
        font-size: 36px;
    }

    @media (min-width: 768px) {
        font-size: 48px;
    }

    & + h1,
    & + h2,
    & + p {
      margin-top: 21px;
    }
`

export const Text = styled.p`
  margin-top: 0;
  font-size: 16px;

  @media (min-width: 768px) {
    font-size: 18px;
  }

  @media (min-width: 992px) {
    font-size: 21px;
  }

  & + & {
    margin-top: 32px;
  }
`
